
var mongoose = require('mongoose');  

var Schema = mongoose.Schema; 
var stationSchema = mongoose.Schema({
	name	: 	String,
	number	: 	Number,
	lat 	: 	Number,
	lon 	: 	Number,
	total 	: 	Number,
	free 	: 	Number,
	bike 	: 	Number,
	date 	: 	Number,
	bonus	: 	Boolean
})
mongoose.connect('mongodb://localhost:27017/nodeAndroid'); 
module.exports = mongoose.model('stations', stationSchema);
